            count : 100,
            soldCount: 1
        }
      //  console.log("constructor", this)

        this.addButton = this.addButton.bind(this);
        this.minusButton = this.minusButton.bind(this);
        this.resetButton = this.resetButton.bind(this);
    }

    addButton() {

this.setState({

    soldCount: this.state.soldCount + 1

});
